pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./Core.sol";
import "./base/ModuleBase.sol";
import "./interfaces/IStatusModule.sol";
import "./base/Types.sol";

contract StatusModuleState {
    DealStatus internal _status_;
    uint256 internal _startWorkingEpoch_;
}

contract StatusModuleInternal is ModuleBase, StatusModuleState {
    // ----------------- Private -----------------

    function _onStartWorking() internal {
        _startWorkingEpoch_ = _core().configModule().globalConfig().epochManager().currentEpoch();
    }

    function _onEndWorking() internal {
        _startWorkingEpoch_ = 0;
    }
}

contract StatusModule is StatusModuleInternal, IStatusModule {
    // ----------------- View -----------------
    function status() external view returns (DealStatus) {
        return _status_;
    }

    function startWorkingEpoch() external view returns (uint256) {
        return _startWorkingEpoch_;
    }

    // ----------------- Mutable -----------------
    function changeStatus(DealStatus status_) external onlyModule(Module.Workers) {
        DealStatus oldStatus = _status_;

        if (oldStatus == status_) {
            return;
        }

        if (oldStatus != status_ && status_ == DealStatus.Working) {
            _onStartWorking();
        } else if (oldStatus != status_ && status_ == DealStatus.WaitingForWorkers) {
            _onEndWorking();
        }

        _status_ = status_;

        emit StatusChanged(status_);
    }
}
","keccak256":"0xe4d6a6a87449e9f75c1ab8ec7ccb2ce8d319585b04687935be0ce7f88f3b261f","license":"Apache-2.0"},"contracts/deal/base/ModuleBase.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "./Types.sol";
import "../interfaces/ICore.sol";

contract ModuleBase is UUPSUpgradeable {
    bytes32 private constant _CORE_SLOT = keccak256("network.fluence.proxy.core");

    modifier onlyModule(Module module) {
        require(_core().moduleByType(module) == msg.sender, "ModuleBase: caller is not required module");
        _;
    }

    modifier onlyModules(Module[] calldata modules) {
        Module callerModule = _core().moduleByAddress(msg.sender);

        bool isRequiredModule = false;
        for (uint256 i = 0; i < modules.length; i++) {
            if (modules[i] != callerModule) {
                continue;
            }

            isRequiredModule = true;
            break;
        }

        require(isRequiredModule, "ModuleBase: caller is not required module");
        _;
    }

    modifier onlyOwner() {
        require(_core().owner() == msg.sender, "ModuleBase: caller is not owner");
        _;
    }

    modifier onlyCore() {
        require(address(_core()) == msg.sender, "ModuleBase: caller is not core");
        _;
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyCore {
        //TODO: check that new implementation from DAO
    }

    function _core() internal view returns (ICore) {
        return ICore(StorageSlot.getAddressSlot(_CORE_SLOT).value);
    }

    uint256[50] private __gap;
}
